import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import carNames from "./cars";

const arr = [1,2,3,4];

function App() {

    const [x, setx] = useState(arr);
    const [y, sety] = useState(arr);
    function changeY(){
        setx(y.filter(item=>item%2===0));
    }
    return <div>
        <h1>{x.map(item=>item+" ")}</h1>
        <button onClick={changeY}>Add</button>
    </div>;
}


export default App;
