import React from "react";
import { useState } from "react";
import carNames from "./cars";

function App() {

    const [newNote, setNewNote] = useState();
    const [list, setList] = useState([]);

    function handleAdd() {
        setList((prevValue) => {
            return [
                ...prevValue,
                newNote,
            ]
        });
    }

    function handleChange(event) {
        const obj = {
            note: event.target.value
        }
        setNewNote(obj);
    }

    return <div>
        <input name="inputNote" placeholder="Type Your Note" onChange={handleChange} />
        <button onClick={handleAdd}>+</button>
        {list.map((item, index) => (
            <div key={index} onClick={handleDelete}>
                <h1 key={index}>{item.note}</h1>
            </div>
        ))}
    </div>;
}


export default App;
