import React from "react";
import { useState } from "react";
import carNames from "./cars";

function App() {

    const [newNote, setNewNote] = useState();
    const [list, setList] = useState([]);

    function handleAdd() {
        setList((prevValue) => {
            return [
                ...prevValue,
                newNote,
            ]
        });
    }
    const [checkLine, setCheckLine] = useState(false);
    function drawLine(e){
        setCheckLine((prev)=>{
            return !prev
        });
    }

    function handleChange(event){
        const obj = {
            note:event.target.value
        }
        setNewNote(obj);
    }

    return <div>
        <input name="inputNote" placeholder="Type Your Note" onChange={handleChange}  />
        <button onClick={handleAdd}>+</button>
        {list.map((item, index) => (
            <div>
                <h1 key={index}>{item.note}</h1>
            </div>
        ))}
    </div>;
}


export default App;
