import React from "react";
import { useState } from "react";
import carNames from "./cars";


function App() {

    const [newNote, setNewNote] = useState("");
    const [list, setList] = useState([]);

    function handleAdd() {
        setList((prevValue) => {
            return [
                ...prevValue,
                newNote,
            ]
        });
        console.log(list);
    }


    return <div>
        <input name="inputNote" placeholder="Type Your Note" onChange={event => setNewNote(event.target.value)}  onClick={(event)=> console.log(event.target.value)}/>
        <button onClick={handleAdd}>+</button>
        {list.map((item) => (
            <div>
                <h1>{item}</h1>
            </div>
        ))}
    </div>;
}


export default App;
