    // }
    function handleClickSub(event){
        appendX(x.substring(0,x.length-1));
    }

    return <div>
        <h1>{x==='yy'?"Ss":x}</h1>
        <button onClick={ function()  {appendX(x+'y');}}>+</button>
        <button onClick={handleClickSub}>-</button>
    </div>
}


export default App;
