import React from "react";
import { useState } from "react";
import carNames from "./cars";

function App() {

    const arr = [1,2,3,4,5];
    console.log(arr.map(item=>item));

    const [newNote, setNewNote] = useState("");
    const [list, setList] = useState([]);

    function handleAdd() {
        setList((prevValue) => {
            return [
                ...prevValue,
                newNote,
            ]
        });
        console.log(list);
    }
    const [checkLine, setCheckLine] = useState(false);
    function drawLine(e){
        setCheckLine((prev)=>{
            return !prev
        });
    }

    return <div>
        <input name="inputNote" placeholder="Type Your Note" onChange={event => setNewNote(event.target.value)}  />
        <button onClick={handleAdd}>+</button>
        {list.map((item) => (
            <div>
                <h1 key={item} onClick={drawLine} style={{textDecorationLine: checkLine?"line-through":null}}>{item}</h1>
            </div>
        ))}
    </div>;
}


export default App;
