import React from "react";
import { useState } from "react";
import Lasers from "./Lasers";

function App(){

    const [should_I_Activate, activate] = useState(false);
    function activateLasers(){
        activate(true);
    }
    function deactivateLasers(){
        activate(false);
    }

    return <div>
        {should_I_Activate?<Lasers />:<h1>Nope</h1>}
        <div>
        <button onClick={activateLasers}>Activate Lasers</button>
        <button onClick={deactivateLasers}>Deactivate Lasers</button>
        </div>
        </div>
}


export default App;
