import React from "react";
import { useState } from "react";
import carNames from "./cars";


function App() {

    const [newNote, setNewNote] = useState("");
    const [list, setList] = useState({
        note: newNote
    });

    function handleAdd() {
        setList((prevValue)=>{
            return {
                ...prevValue,
                note:newNote
            }
        })
        console.log(list);
    }

    return <div>
        <input name="inputNote" placeholder="Type Your Note" onChange={event => setNewNote(event.target.value)} />
        <button onClick={handleAdd}>+</button>
        {list.map((item)=>(
            <h1>{item.note}</h1>
        ))}
    </div>;
}


export default App;
