import React from "react";
import { useState } from "react";
import carNames from "./cars";

const allNotesOriginal = [];

function App() {
    const [allNotes,setAllNotes] = useState([""]);
    const [note, setNote] = useState("");

    function handleAddNote(){
        allNotesOriginal.push(note);
        setAllNotes(allNotesOriginal);
        console.log(allNotesOriginal);
    }
    return <div>
        <input type="text" placeholder="Type your note" onChange={(e)=>setNote(e.target.value)}/>
        <button onClick={handleAddNote}>Add</button>
        <div>
            {allNotes.map(
                (item)=>{return <div>
                <h1 key={item.id}>{item}</h1>
                </div>
                }
            )}
        </div>

    </div>;
}


export default App;
